<script setup>
const products = ref([]);
const getProducts = async () => {
    refreshNuxtData();
    try {
        const { pending, data } = await useFetch(`${useRuntimeConfig().public.baseUrl}/filter`, {
            method: 'POST',
            body: {
                marker: ['deals-month'],
            }
        });
        products.value = data.value.data;
    } catch (error) {
        console.log(error);
    }
}
getProducts();

const value = (index) => {

    if (index === 1) {
        return 'border-r border-l border-b';

    } else if (index === 3) {
        return ' border-t border-r';
    } else if (index === 0) {
        return 'border-b';
    } else if (index === 2) {
        return 'border-b';
    }
    else if (index === 3) {
        return 'border-r border-l border-t';
    }

    else if (index === 4) {
        return 'border-r border-l border-t';
    }

    else if (index === 5) {
        return ' border-l ';
    }



    else {
        return '';
    }
};

</script>
